import React from 'react';
import styled from 'styled-components';
import { rem } from 'polished';
import PropTypes from 'prop-types';

const Footer = ({ children }) => {
  return (
    <StyledFooter>
      <div className="center-text">&copy;{children}</div>
    </StyledFooter>
  );
};

const StyledFooter = styled.footer`
  text-align: center;
  background-color: ${({ theme }) => theme.__OriginateLib.colors.whity};
  color: ${({ theme }) => theme.__OriginateLib.colors.primary};
  display: flex;
  font-weight: ${({ theme }) => theme.__OriginateLib.fontWeight.semibold};
  height: ${rem('105px')};
  line-height: ${rem('25px')};
  position: relative;
`;
Footer.propTypes = {
  className: PropTypes.string
};
export default Footer;
