import React from 'react';
import PropTypes from 'prop-types';
import { Step } from 'react-albus/lib';

const SignUpStep = props => {
  return (
    <Step>
      <div className="sign-up-step">
        {!props.title ? (
          ''
        ) : (
          <h4
            className={`sign-up-step__title ${
              props.smallTitle ? 'small-title' : ''
            }`}
          >
            {props.title}
          </h4>
        )}
        <div className={'sign-up-step__content'}>{props.children}</div>
        {props.note ? <aside className="note">*NOTE: {props.note}</aside> : ''}
      </div>
    </Step>
  );
};

SignUpStep.propTypes = {
  note: PropTypes.string,
  title: PropTypes.string,
  classes: PropTypes.string,
  smallTitle: PropTypes.oneOfType([PropTypes.bool, PropTypes.string]),
  id: PropTypes.string.isRequired
};

export { SignUpStep };
