import React from 'react';
import PropTypes from 'prop-types';
import styled from 'styled-components';
import { rem } from 'polished';

const Table = ({ tableHeaders, children }) => {
  return (
    <StyledTable>
      {tableHeaders === '' || tableHeaders == null ? (
        ''
      ) : (
        <thead>
          <tr>
            {tableHeaders.map(val => (
              <th key={val}>{val}</th>
            ))}
          </tr>
        </thead>
      )}
      {children}
    </StyledTable>
  );
};

Table.propTypes = {
  tableHeaders: PropTypes.array
};

const StyledTable = styled.table`
  width: 100%;

  thead {
    tr {
      th {
        color: ${({ theme }) => theme.__OriginateLib.colors.text2};
        font-size: ${rem('12px')};
        line-height: 30px;
        letter-spacing: 0.05em;
        padding: ${rem('15px')};
        text-align: left;
        white-space: nowrap;

        &:first-of-type {
          padding-left: ${rem('25px')};
        }

        &:last-of-type {
          padding-right: ${rem('25px')};
        }
      }
    }
  }

  tbody {
    tr {
      td {
        border-bottom: 1px solid
          ${({ theme }) => theme.__OriginateLib.colors.disabled};
        font-size: ${rem('14px')};
        font-weight: ${({ theme }) => theme.__OriginateLib.fontWeight.semiBold};
        line-height: 30px;
        padding: ${rem('15px')};

        &:first-of-type {
          padding-left: ${rem('25px')};
        }

        &:last-of-type {
          padding-right: ${rem('25px')};
        }
      }
    }
  }

  button,
  .button {
    padding: ${rem('6px')} ${rem('25px')};
    border: 1px solid ${({ theme }) => theme.__OriginateLib.colors.primary};
    border-radius: ${rem('4px')};

    @include phone {
      padding: ${rem('2px')} ${rem('10px')};
    }
  }

  .first-td {
    color: ${({ theme }) => theme.__OriginateLib.colors.text2};
  }
`;

export default Table;
